CREATE FUNCTION ipa_ops.filestore_report_select_fields(cid integer, altype character varying, alid integer, sfid integer, afid integer) RETURNS jsonb
    LANGUAGE plpgsql
    AS $$
 DECLARE
        jo jsonb;
        joid jsonb;
