    RETURN NEW;

  END;
$$;


--
-- Name: get_adl_screener_master_id(numeric); Type: FUNCTION; Schema: tbs; Owner: -
--

CREATE FUNCTION tbs.get_adl_screener_master_id(subject_id numeric) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
  matched_master_id INTEGER;
BEGIN


		SELECT ipa.master_id
    INTO matched_master_id
		FROM ml_app.masters m
		INNER JOIN tbs.tbs_assignments ipa
			ON m.id = ipa.master_id
		WHERE
      ipa.tbs_id = subject_id
    LIMIT 1
		;

    RETURN matched_master_id;
END;
$$;


